    vt_group_numbers=$(time mysql -h"$DB_HOST" -u"$DB_USER" -p"$DB_PASS" -D"$DB_NAME" -sN -e "SELECT vt_group_no FROM orders WHERE ticketid = '$ticket_id' and status = '0' and vt_group_no = '171402682349905'") || exit 1
    
